import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import Chart from 'chart.js/auto'; // Import Chart.js
import './styles.css'; // Import the CSS file

const App: React.FC = () => {
  const [farms, setFarms] = useState<any[]>([]);
  const [otherData, setOtherData] = useState<any[]>([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8080/api/farms')
      .then(response => {
        setFarms(response.data.farms); // Extract farms data from response
        setOtherData(response.data.other_data); // Extract other_data from response
        renderChart(response.data.farms); // Render chart after fetching data
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const renderChart = (data: any[]) => {
    const ctx = document.getElementById('myChart') as HTMLCanvasElement;
    const myChart = new Chart(ctx, {
      type: 'pie',
      data: {
        labels: data.map(farm => farm.premiseid),
        datasets: [{
          label: 'Total Animals',
          data: data.map(farm => farm.total_animal),
          backgroundColor: [
            'rgba(255, 99, 132, 0.5)',
            'rgba(54, 162, 235, 0.5)',
            'rgba(255, 206, 86, 0.5)',
            'rgba(75, 192, 192, 0.5)',
            'rgba(153, 102, 255, 0.5)',
            'rgba(255, 159, 64, 0.5)'
          ],
          hoverOffset: 4
        }]
      },
      options: {
        animation: {
          animateRotate: true, // Enable rotation animation
          animateScale: true // Enable scaling animation
        }
      }
    });
  };

  return (
    <div className="App">
      <h1>Animal Movement Tracker</h1>
      <div>
        <h2>Farms</h2>
        <table>
          <thead>
            <tr>
              <th>Premise ID</th>
              <th>Total Animals</th>
            </tr>
          </thead>
          <tbody>
            {farms.map((farm: any, index: number) => (
              <tr key={index}>
                <td>{farm.premiseid}</td>
                <td>{farm.total_animal}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div>
        <h2>Other Data</h2>
        <table>
          <thead>
            <tr>
              <th>New Origin Premise ID</th>
              <th>New Destination Premise ID</th>
              <th>Number of Items Moved</th>
            </tr>
          </thead>
          <tbody>
            {otherData.map((data: any, index: number) => (
              <tr key={index}>
                <td>{data.new_originpremid}</td>
                <td>{data.new_destinationpremid}</td>
                <td>{data.new_numitemsmovedcolumn}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div>
        <h2>Chart</h2>
        <canvas id="myChart" width="400" height="200"></canvas>
      </div>
    </div>
  );
};

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
